extension KeyboardViewController: UIKeyInput {
    var hasText: Bool {
        textDocumentProxy.hasText
    }

    func insertText(_ text: String) {
        textDocumentProxy.insertText(text)
    }

    func deleteBackward() {
        textDocumentProxy.deleteBackward()
    }
